import './App.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import React from 'react';
import axios from 'axios';

//import Modal from 'react-bootstrap/Modal';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userInput: '',
      cityData: '',
      display_name: '',
      latitude: '',
      longitude: '',
      errorMessage: '',
      displayError: false
    }
  }

  getData = async (e) => {
    e.preventDefault();
    const allCity = await axios.get(`https://us1.locationiq.com/v1/search?&key=${process.env.TOKEN}q=${e.target.control.value}&format=json`)
    console.log(allCity);
    this.setState({
      userInput: e.target.control.value,
      cityData: allCity.data[0],
      display_name: allCity.data[0].display_name,
      latitude: allCity.data[0].lat,
      longitude: allCity.data[0].lon,
      displayError: false
    })
  }


  render() {
    return (
      <>
        <h1>{process.env.REACT_APP_TITLE}</h1>
        <Form onSubmit={this.getData} id="control" className='form'>
          <Form.Group className="mb-3" >
            <Form.Label>City Explorer: </Form.Label>
            <Form.Control id="control" type="explorer" placeholder="Enter city name ..." />
          </Form.Group>
          <Button className='button' variant="primary" type="submit">
            Explorer!
          </Button>
        </Form>

        <div className='contaner'>

          <h1> cityData= {this.props.cityData}</h1>

        </div >
      </>
    );
  }
}
export default App;
